commits.body:
   add_scope () {
      {  pl &lt;"$1"|egrep -v 'scope = |^} *$'
         echo "    scope = \"source.$2\";"
         echo "}"
      } >"$1_new"
      mv "$1_new" "$1"
   }

   for file in Snippets/*.plist; do add_scope $file NAME; done



